import React, { useState, useEffect } from "react";
import { ExternalLink, Star, TrendingUp, Zap, Shield, Award } from "lucide-react";

const ToolCard = ({ tool }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [isClicked, setIsClicked] = useState(false);
  const [particles, setParticles] = useState([]);

  // Generate floating particles on hover
  useEffect(() => {
    if (isHovered) {
      const newParticles = Array.from({ length: 8 }, (_, i) => ({
        id: i,
        x: Math.random() * 100,
        y: Math.random() * 100,
        delay: Math.random() * 2,
        duration: 3 + Math.random() * 2
      }));
      setParticles(newParticles);
    } else {
      setParticles([]);
    }
  }, [isHovered]);

  const handleMouseMove = (e) => {
    const card = e.currentTarget;
    const rect = card.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    const centerX = rect.width / 2;
    const centerY = rect.height / 2;
    const rotateX = ((y - centerY) / centerY) * 8;
    const rotateY = ((x - centerX) / centerX) * 8;
    
    card.style.transform = `
      perspective(1000px) 
      rotateX(${-rotateX}deg) 
      rotateY(${rotateY}deg) 
      scale(1.02)
      translateZ(20px)
    `;
    
    // Update CSS custom properties for gradient following mouse
    card.style.setProperty('--mouse-x', `${x}px`);
    card.style.setProperty('--mouse-y', `${y}px`);
  };

  const handleMouseLeave = (e) => {
    e.currentTarget.style.transform = "perspective(1000px) rotateX(0deg) rotateY(0deg) scale(1) translateZ(0px)";
    setIsHovered(false);
  };

  const handleButtonClick = (e) => {
    e.stopPropagation();
    setIsClicked(true);
    
    // Create ripple effect
    const btn = e.currentTarget;
    const ripple = document.createElement("span");
    const rect = btn.getBoundingClientRect();
    const size = Math.max(rect.width, rect.height);
    const x = e.clientX - rect.left - size / 2;
    const y = e.clientY - rect.top - size / 2;
    
    ripple.style.cssText = `
      position: absolute;
      left: ${x}px;
      top: ${y}px;
      width: ${size}px;
      height: ${size}px;
      background: rgba(255,255,255,0.6);
      border-radius: 50%;
      transform: scale(0);
      pointer-events: none;
      animation: ripple 0.6s ease-out;
    `;
    
    btn.appendChild(ripple);
    setTimeout(() => ripple.remove(), 600);
    setTimeout(() => setIsClicked(false), 300);
  };

  const getCategoryIcon = (category) => {
    const icons = {
      'AI': <Zap className="w-4 h-4" />,
      'Design': <Award className="w-4 h-4" />,
      'Development': <TrendingUp className="w-4 h-4" />,
      'Security': <Shield className="w-4 h-4" />,
      'Analytics': <TrendingUp className="w-4 h-4" />
    };
    return icons[category] || <Star className="w-4 h-4" />;
  };

  return (
    <div className="relative p-1 group">
      {/* Animated border gradient */}
      <div className="absolute inset-0 rounded-2xl bg-gradient-to-r from-purple-500 via-pink-500 to-blue-500 opacity-0 group-hover:opacity-100 blur-sm transition-all duration-500 animate-pulse"></div>
      
      <div
        className="relative bg-white/95 backdrop-blur-sm rounded-2xl p-6 shadow-xl border border-gray-200/50 cursor-pointer transition-all duration-500 ease-out overflow-hidden"
        onMouseEnter={() => setIsHovered(true)}
        onMouseMove={handleMouseMove}
        onMouseLeave={handleMouseLeave}
        style={{
          '--mouse-x': '0px',
          '--mouse-y': '0px'
        }}
      >
        {/* Animated background gradient that follows mouse */}
        <div 
          className="absolute inset-0 opacity-0 group-hover:opacity-10 transition-opacity duration-300"
          style={{
            background: `radial-gradient(circle 200px at var(--mouse-x) var(--mouse-y), 
              rgba(147, 51, 234, 0.3) 0%, 
              rgba(59, 130, 246, 0.2) 50%, 
              transparent 100%)`
          }}
        ></div>

        {/* Floating particles */}
        {particles.map((particle) => (
          <div
            key={particle.id}
            className="absolute w-1 h-1 bg-gradient-to-r from-purple-400 to-pink-400 rounded-full opacity-60"
            style={{
              left: `${particle.x}%`,
              top: `${particle.y}%`,
              animation: `float ${particle.duration}s ease-in-out infinite ${particle.delay}s`
            }}
          ></div>
        ))}

        {/* Header with category and price */}
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-2 px-3 py-1 bg-gradient-to-r from-purple-100 to-pink-100 rounded-full border border-purple-200/50 group-hover:from-purple-200 group-hover:to-pink-200 transition-all duration-300">
            <div className="text-purple-600 group-hover:scale-110 transition-transform duration-300">
              {getCategoryIcon(tool.category)}
            </div>
            <span className="text-sm font-medium text-purple-800">{tool.category}</span>
          </div>
          
          <div className="text-right">
            <div className="text-2xl font-bold bg-gradient-to-r from-green-600 to-emerald-600 bg-clip-text text-transparent group-hover:from-green-500 group-hover:to-emerald-500 transition-all duration-300">
              {tool.price}
            </div>
            {tool.originalPrice && (
              <div className="text-sm text-gray-500 line-through">{tool.originalPrice}</div>
            )}
          </div>
        </div>

        {/* Title with gradient text */}
        <h3 className="text-xl font-bold mb-3 bg-gradient-to-r from-gray-900 to-gray-700 bg-clip-text text-transparent group-hover:from-purple-800 group-hover:to-pink-800 transition-all duration-300">
          {tool.name}
        </h3>

        {/* Description with fade animation */}
        <p className="text-gray-600 mb-6 leading-relaxed group-hover:text-gray-700 transition-colors duration-300">
          {tool.description}
        </p>

        {/* Stats or features */}
        {tool.stats && (
          <div className="flex items-center gap-4 mb-6 text-sm text-gray-500">
            {tool.stats.map((stat, index) => (
              <div key={index} className="flex items-center gap-1">
                <Star className="w-3 h-3 text-yellow-500" />
                <span>{stat}</span>
              </div>
            ))}
          </div>
        )}

        {/* Action button with enhanced animations */}
        <button
          onClick={handleButtonClick}
          className={`
            relative w-full py-3 px-6 rounded-xl font-semibold text-white overflow-hidden
            bg-gradient-to-r from-purple-600 to-pink-600 
            hover:from-purple-700 hover:to-pink-700
            transform transition-all duration-300 ease-out
            hover:scale-105 hover:shadow-2xl
            ${isClicked ? 'scale-95' : ''}
            group/button
          `}
        >
          {/* Button background shine effect */}
          <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent -translate-x-full group-hover/button:translate-x-full transition-transform duration-700 ease-out"></div>
          
          <div className="relative flex items-center justify-center gap-2">
            <span className="group-hover/button:scale-110 transition-transform duration-200">
              Visit Site
            </span>
            <ExternalLink className="w-4 h-4 group-hover/button:translate-x-1 group-hover/button:-translate-y-1 transition-transform duration-300" />
          </div>
        </button>

        {/* Pulse effect on click */}
        {isClicked && (
          <div className="absolute inset-0 rounded-2xl border-2 border-purple-400 animate-ping"></div>
        )}
      </div>

      {/* Custom CSS for animations */}
      <style jsx>{`
        @keyframes ripple {
          to {
            transform: scale(4);
            opacity: 0;
          }
        }
        @keyframes float {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          25% { transform: translateY(-10px) rotate(90deg); }
          50% { transform: translateY(-20px) rotate(180deg); }
          75% { transform: translateY(-10px) rotate(270deg); }
        }
      `}</style>
    </div>
  );
};

// Demo component with sample data
const ToolCardDemo = () => {
  const sampleTools = [
    {
      id: 1,
      name: "AI Content Generator",
      category: "AI",
      price: "$29/mo",
