import React from "react";

const ToolCard = ({ tool }) => (
  <div
    className="tool-card"
    tabIndex={0}
    style={{
      boxShadow: "0 4px 24px rgba(0,0,0,0.10)",
      transition: "box-shadow 0.3s, transform 0.3s, border 0.2s, background 0.4s",
      outline: "none",
      background: "linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)",
      borderRadius: "18px",
      border: "2px solid transparent",
      overflow: "hidden",
      position: "relative",
    }}
    onMouseMove={e => {
      // Card tilt effect
      const card = e.currentTarget;
      const rect = card.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;
      const centerX = rect.width / 2;
      const centerY = rect.height / 2;
      const rotateX = ((y - centerY) / centerY) * 6;
      const rotateY = ((x - centerX) / centerX) * 6;
      card.style.transform = `rotateX(${-rotateX}deg) rotateY(${rotateY}deg) scale(1.045)`;
      card.style.background =
        "linear-gradient(135deg, #e3f0ff 0%, #fafcff 100%)";
    }}
    onMouseLeave={e => {
      e.currentTarget.style.transform = "none";
      e.currentTarget.style.background =
        "linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)";
    }}
    onFocus={e => {
      e.currentTarget.style.boxShadow =
        "0 12px 36px rgba(25,118,210,0.18), 0 2px 8px rgba(25,118,210,0.09)";
      e.currentTarget.style.border = "2px solid #1976d2";
      e.currentTarget.style.background =
        "linear-gradient(135deg, #e3f0ff 0%, #fafcff 100%)";
    }}
    onBlur={e => {
      e.currentTarget.style.boxShadow =
        "0 4px 24px rgba(0,0,0,0.10)";
      e.currentTarget.style.border = "2px solid transparent";
      e.currentTarget.style.background =
        "linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)";
    }}
  >
    {/* Animated Glow Border */}
    <span
      style={{
        position: "absolute",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        borderRadius: "18px",
        pointerEvents: "none",
        zIndex: 0,
        boxShadow:
          "0 0 0 0 rgba(25,118,210,0.0), 0 0 32px 4px rgba(25,118,210,0.08)",
        animation: "glow 2.5s ease-in-out infinite alternate",
      }}
    />
    {/* Category Badge with Animation */}
    <span
      className="category-badge"
      style={{
        background: "linear-gradient(90deg, #1976d2 60%, #42a5f5 100%)",
        color: "#fff",
        fontWeight: 600,
        borderRadius: "8px 0 8px 0",
        padding: "4px 12px",
        fontSize: 13,
        position: "absolute",
        left: 16,
        top: 16,
        boxShadow: "0 2px 8px rgba(25,118,210,0.10)",
        letterSpacing: 1,
        zIndex: 2,
        animation: "badgePop 0.7s cubic-bezier(.68,-0.55,.27,1.55)"
      }}
    >
      {tool.category}
    </span>
    {/* Price Badge with Animation */}
    <span
      className="price-badge"
      style={{
        background: "#fff",
        color: "#1976d2",
        fontWeight: 700,
        borderRadius: "0 8px 0 8px",
        padding: "4px 12px",
        fontSize: 13,
        position: "absolute",
        right: 16,
        top: 16,
        boxShadow: "0 2px 8px rgba(25,118,210,0.10)",
        letterSpacing: 1,
        zIndex: 2,
        animation: "badgePop 0.7s cubic-bezier(.68,-0.55,.27,1.55)"
      }}
    >
      {tool.price}
    </span>
    <h2
      style={{
        margin: "56px 0 12px 0",
        fontSize: 22,
        fontWeight: 800,
        color: "#222",
        letterSpacing: 0.5,
        textShadow: "0 2px 8px rgba(25,118,210,0.08)",
        zIndex: 2,
        position: "relative",
        animation: "fadeInUp 0.7s cubic-bezier(.68,-0.55,.27,1.55)"
      }}
    >
      {tool.name}
    </h2>
    <p
      style={{
        color: "#444",
        fontSize: 15,
        margin: "0 0 28px 0",
        zIndex: 2,
        position: "relative",
        minHeight: 48,
        animation: "fadeInUp 0.9s cubic-bezier(.68,-0.55,.27,1.55)"
      }}
    >
      {tool.description}
    </p>
    <a
      href={tool.link}
      target="_blank"
      rel="noreferrer"
      tabIndex={0}
      style={{
        position: "relative",
        display: "inline-block",
        background: "linear-gradient(90deg, #1976d2 60%, #42a5f5 100%)",
        color: "#fff",
        fontWeight: 700,
        borderRadius: "8px",
        padding: "10px 28px 10px 20px",
        fontSize: 15,
        boxShadow: "0 2px 8px rgba(25,118,210,0.10)",
        border: "none",
        outline: "none",
        cursor: "pointer",
        marginBottom: 10,
        marginTop: 8,
        overflow: "hidden",
        zIndex: 2,
        transition: "background 0.3s, transform 0.2s, box-shadow 0.2s"
      }}
      onMouseDown={e => {
        // Button ripple effect
        const btn = e.currentTarget;
        const ripple = document.createElement("span");
        ripple.className = "ripple";
        ripple.style.position = "absolute";
        ripple.style.left = `${e.nativeEvent.offsetX}px`;
        ripple.style.top = `${e.nativeEvent.offsetY}px`;
        ripple.style.width = ripple.style.height = "0px";
        ripple.style.background = "rgba(255,255,255,0.4)";
        ripple.style.borderRadius = "50%";
        ripple.style.transform = "translate(-50%, -50%)";
        ripple.style.pointerEvents = "none";
        ripple.style.transition = "width 0.5s, height 0.5s, opacity 0.5s";
        btn.appendChild(ripple);
        setTimeout(() => {
          ripple.style.width = ripple.style.height = "120px";
          ripple.style.opacity = "0";
        }, 10);
        setTimeout(() => {
          ripple.remove();
        }, 510);
      }}
      onMouseEnter={e => {
        e.currentTarget.style.transform = "scale(1.07)";
        e.currentTarget.style.boxShadow = "0 6px 24px rgba(25,118,210,0.18)";
        e.currentTarget.style.background =
          "linear-gradient(90deg, #42a5f5 0%, #1976d2 100%)";
      }}
      onMouseLeave={e => {
        e.currentTarget.style.transform = "none";
        e.currentTarget.style.boxShadow = "0 2px 8px rgba(25,118,210,0.10)";
        e.currentTarget.style.background =
          "linear-gradient(90deg, #1976d2 60%, #42a5f5 100%)";
      }}
    >
      <span
        style={{
          display: "inline-block",
          transition: "transform 0.2s",
        }}
      >
        Visit Site
        <svg
          style={{ marginLeft: 6, verticalAlign: "middle" }}
          width="16"
          height="16"
          fill="#fff"
          viewBox="0 0 16 16"
        >
          <path d="M6.354 13.354a.5.5 0 0 1-.708-.708l6.647-6.646H9.5a.5.5 0 0 1 0-1h4a.5.5 0 0 1 .5.5v4a.5.5 0 0 1-1 0V7.061l-6.646 6.647z" />
          <path d="M13.5 2a.5.5 0 0 1 .5.5v11a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11A.5.5 0 0 1 2.5 2h11zm-11-1A1.5 1.5 0 0 0 1 2.5v11A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-11A1.5 1.5 0 0 0 13.5 1h-11z" />
        </svg>
      </span>
    </a>
    {/* Keyframes for animations */}
    <style>{`
      @keyframes glow {
        0% { box-shadow: 0 0 0 0 rgba(25,118,210,0.0), 0 0 32px 4px rgba(25,118,210,0.08); }
        100% { box-shadow: 0 0 0 8px rgba(25,118,210,0.10), 0 0 48px 8px rgba(25,118,210,0.16); }
      }
      @keyframes badgePop {
        0% { transform: scale(0.7); opacity: 0; }
        80% { transform: scale(1.1); opacity: 1; }
        100% { transform: scale(1); opacity: 1; }
      }
      @keyframes fadeInUp {
        0% { opacity: 0; transform: translateY(24px); }
        100% { opacity: 1; transform: translateY(0); }
      }
      .ripple {
        pointer-events: none;
        z-index: 10;
      }
    `}</style>
  </div>
);

export default ToolCard;
